

INSERT INTO email_templates(template_name, email_subject, email_body)
VALUES ('FORGOT_PASSWORD', 'Reset your JJCSA USA Password',
'<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml"><head><meta http-equiv="Content-Type" content="text/html; charset=utf-8"><meta http-equiv="X-UA-Compatible" content="IE=edge"><meta name="viewport" content="width=device-width,initial-scale=1"><link href="https://fonts.googleapis.com/css?family=Noto Sans" rel="stylesheet"><title>JJCSA Welcome template</title><style type="text/css">#outlook a{padding:0}.ReadMsgBody{width:100%}.ExternalClass{width:100%}.ExternalClass *{line-height:100%}body{margin:0;padding:0;-webkit-text-size-adjust:100%;-ms-text-size-adjust:100%}table,td{border-collapse:collapse;mso-table-lspace:0;mso-table-rspace:0}img{border:0;height:auto;line-height:100%;outline:0;text-decoration:none;-ms-interpolation-mode:bicubic}p{display:block}body{Margin:0;padding:0;background-color:#e5e5e5}table{border-spacing:0;border-collapse:collapse!important}.wrapper{width:100%;table-layout:fixed;background-color:#e5e5e5;align-items:center;Margin:0 auto;text-align:center;align-items:center}.webkit{max-width:600px;background-color:#244f90;Margin:0 auto;font-family:"Noto Sans";padding-bottom:20px}.outer{Margin:0 auto;width:100%;max-width:600px;border-spacing:0}.inner{Margin:0 auto;border-spacing:0;color:#fff;padding:20px}.center{display:flex!important;justify-content:center!important;text-align:center!important}nav ul{display:flex}.reset-password-button{display:block;width:250px;height:25px;background:#244f90;padding:10px;text-align:center;border-radius:5px;color:#fff;font-weight:700;line-height:25px;text-decoration:none}</style></head><body><section class="wrapper"><div class="webkit"><table class="outer"><tr><td><table width="100%" style="border-spacing:0"><tr><td align="center" style="background-color:#244f90;padding:10px;color:#fff;font-size:18px;display:inline-block;vertical-align:top;direction:ltr"><div style="display:flex"><img src="https://jjc-email-template.s3.amazonaws.com/Bitmap.png"><p>JJC STUDENT ASSOCIATION</p></div></td></tr></table></td></tr><table class="inner"><tr><td><table><tr><td width="100%"><table style="Margin:0 auto" width="90%" cellpadding="25" cellspacing="0"><tr style="background-color:#fff"><td style="padding-left:20px;padding-right:10px;text-align:left"><p style="color:#4a4a4a;line-height:19px;margin-top:0;padding:0;margin-bottom:25px">Hello,</p><p style="color:#4a4a4a;line-height:19px;margin-top:0;padding:0;margin-bottom:25px">We''ve received a request to reset the password for the JJCSA USA account associated with<span th:text="${email}"></span>. No changes have been made to your account yet.</p><p style="color:#4a4a4a;line-height:19px;margin-top:0;padding:0;margin-bottom:25px">You can reset your password by clicking the button below:</p><a class="reset-password-button" href="${link}">Reset your password</a><br><table width="100%" border="0" cellpadding="0" cellspacing="0" align="center" class="border-complete deviceWidth"><tr><td align="center" valign="top" id="socials"><table id="social-icons" border="0" cellspacing="0" cellpadding="0"><tr><td><a href="#"><img src="https://jjc-email-template.s3.amazonaws.com/image+1.png" width="32" height="32" style="display:block"></a></td><td><a href="#"><img style="padding-left:10px;display:block" src="https://jjc-email-template.s3.amazonaws.com/image+3.png" width="32" height="32"></a></td><td><a href="#"><img style="padding-left:10px;display:block" src="https://jjc-email-template.s3.amazonaws.com/image+5.png" width="32" height="32"></a></td><td><a href="#"><img style="padding-left:10px;display:block" src="https://jjc-email-template.s3.amazonaws.com/image+6.png" width="32" height="32"></a></td></tr></table></td></tr></table><p style="color:#4a4a4a;line-height:19px;margin-top:0;padding-top:20px;margin-bottom:25px">Wish to volunteer? Need assistance? Have questions? You can reach out to us by sending an email at<span style="color:#244f90!important;text-decoration:none">jjcsausa@gmail.com</span>or Whatsapp.</p><img style="width:100%" data-imagetype="External" src="https://jjc-email-template.s3.amazonaws.com/image+7.png"></td></tr></table></td></tr></table><table></table></td></tr></table></table></div></section></body></html>'
        );

